#include <stdint.h>

uint8_t to_uint_8(int64_t v) {
	return (uint8_t) v;
}

uint16_t to_uint_16(int64_t v) {
	return (uint16_t) v;
}

uint32_t to_uint_32(int64_t v) {
	return (uint32_t) v;
}

int8_t to_int8_t(int64_t v) {
	return (int8_t) v;
}

int16_t to_int16_t(int64_t v) {
	return (int16_t) v;
}

int32_t to_int32_t(int64_t v) {
	return (int32_t) v;
}
